--  DDL for Procedure INSERT_SUBSCRIBED_CHANGES
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "WERCS"."INSERT_SUBSCRIBED_CHANGES" (
    p_changeSource IN varchar2,
    p_changedEntityID IN nvarchar2,
    p_changeType IN varchar2
)
IS
BEGIN

    -- Insert statements for procedure here
	MERGE INTO T_CHANGES changes
	USING (
		SELECT
			subs.F_SUBSCRIBER_ID,
			changes.F_CHANGE_SOURCE,
			changes.F_CHANGED_ENTITY_ID,
			changes.F_CHANGE_TYPE
		FROM
			T_CHANGE_SUBSCRIBERS subs
			CROSS JOIN (
                SELECT p_changeSource F_CHANGE_SOURCE, p_changedEntityID F_CHANGED_ENTITY_ID, p_changeType F_CHANGE_TYPE
                FROM dual
			) changes
		WHERE subs.F_SUBSCRIBE_TO_SOURCE = changes.F_CHANGE_SOURCE
	) inbound ON (
		changes.F_SUBSCRIBER_ID = inbound.F_SUBSCRIBER_ID
			AND changes.F_CHANGE_SOURCE = inbound.F_CHANGE_SOURCE
			AND changes.F_CHANGED_ENTITY_ID = inbound.F_CHANGED_ENTITY_ID
			AND changes.F_CHANGE_TYPE = inbound.F_CHANGE_TYPE
        )
	WHEN NOT MATCHED THEN
		INSERT (F_SUBSCRIBER_ID, F_CHANGED_ENTITY_ID, F_CHANGE_SOURCE, F_CHANGE_TYPE, F_LAST_CHANGE_DATE, F_HANDLED_FLAG)
		VALUES (
			inbound.F_SUBSCRIBER_ID,
			inbound.F_CHANGED_ENTITY_ID,
			inbound.F_CHANGE_SOURCE,
			inbound.F_CHANGE_TYPE,
			sysdate,
			0
		)
	WHEN MATCHED THEN
		UPDATE
		SET F_LAST_CHANGE_DATE = sysdate,
			F_HANDLED_FLAG = 0
	;

END;

