--  DDL for Procedure READONLYWERCS
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "WERCS"."READONLYWERCS" (str_schema_owner IN VARCHAR2, str_readonly_id IN VARCHAR2)
AUTHID CURRENT_USER IS
BEGIN
  FOR I IN (SELECT OWNER, OBJECT_NAME FROM ALL_OBJECTS WHERE OBJECT_TYPE IN('TABLE', 'VIEW') AND OWNER=str_schema_owner AND OBJECT_NAME NOT IN ('T_SEC_USERS','T_PASSWORD_HISTORY','T_EMPLOYEES'))
  LOOP
      EXECUTE IMMEDIATE 'GRANT READ ON '||str_schema_owner||'.'||I.OBJECT_NAME||' TO '||str_readonly_id;
  END LOOP;

  FOR I IN (SELECT OWNER, OBJECT_NAME FROM ALL_OBJECTS WHERE OBJECT_TYPE IN('TABLE', 'VIEW') AND OWNER=str_schema_owner AND OBJECT_NAME IN ('T_SEC_USERS','T_PASSWORD_HISTORY','T_EMPLOYEES'))
  LOOP
      EXECUTE IMMEDIATE 'REVOKE ALL ON '||str_schema_owner||'.'||I.OBJECT_NAME||' FROM '||str_readonly_id;
  END LOOP;

  FOR I IN (SELECT OWNER, OBJECT_NAME FROM ALL_OBJECTS WHERE OBJECT_TYPE IN('FUNCTION', 'PROCEDURE') AND OWNER=str_schema_owner)
  LOOP
      EXECUTE IMMEDIATE 'GRANT EXECUTE ON '||str_schema_owner||'.'||I.OBJECT_NAME||' TO '||str_readonly_id;
  END LOOP;

  FOR I IN (SELECT OWNER, OBJECT_NAME FROM ALL_OBJECTS WHERE OBJECT_TYPE IN('TABLE', 'VIEW') AND OWNER=str_schema_owner
AND NOT EXISTS (SELECT 'X' FROM ALL_SYNONYMS ALLSYN WHERE ALLSYN.TABLE_NAME = ALL_OBJECTS.OBJECT_NAME AND ALLSYN.TABLE_OWNER = ALL_OBJECTS.OWNER
AND ALLSYN.SYNONYM_NAME = ALL_OBJECTS.OBJECT_NAME AND ALLSYN.OWNER = str_readonly_id))
  LOOP
      EXECUTE IMMEDIATE 'CREATE SYNONYM '||str_readonly_id||'.'||I.OBJECT_NAME||' FOR '||I.OWNER||'."'||I.OBJECT_NAME||'"';
  END LOOP;

  FOR I IN (SELECT OWNER, OBJECT_NAME FROM ALL_OBJECTS WHERE OBJECT_TYPE IN('FUNCTION', 'PROCEDURE') AND OWNER=str_schema_owner
AND NOT EXISTS (SELECT 'X' FROM ALL_SYNONYMS ALLSYN WHERE ALLSYN.TABLE_NAME = ALL_OBJECTS.OBJECT_NAME AND ALLSYN.TABLE_OWNER = ALL_OBJECTS.OWNER
AND ALLSYN.SYNONYM_NAME = ALL_OBJECTS.OBJECT_NAME AND ALLSYN.OWNER = str_readonly_id))
  LOOP
      EXECUTE IMMEDIATE 'CREATE SYNONYM '||str_readonly_id||'.'||I.OBJECT_NAME||' FOR '||I.OWNER||'."'||I.OBJECT_NAME||'"';
  END LOOP;
END;

