	int_numFilters int;
	
	tmp_int int;

    v_TFS_106067 boolean;

BEGIN
    v_TFS_106067 := false;
	
	

	
	var_operator := 'AND';

	
	INSERT INTO GTT_MM_INBOUND_FILTERS (
					F_GUID,
					F_USAGE,
					F_DATA_CODE,
					F_TEXT_CODE,
					F_IS_NUMERIC,
					F_COMPARISON,
					F_VALUE,
					F_VALUE_TARGET,
					F_VALUE_NUMERIC,
					F_VALUE_TO,
					F_VALUE_TO_NUMERIC,
					F_POLARITY,
					F_ARRAY
	  )
	  SELECT 	F_GUID,
					F_USAGE,
					F_DATA_CODE,
					F_TEXT_CODE,
					F_IS_NUMERIC,
					F_COMPARISON,
					F_VALUE,
					F_VALUE_TARGET,
					F_VALUE_NUMERIC,
					F_VALUE_TO,
					F_VALUE_TO_NUMERIC,
					F_POLARITY,
					F_ARRAY
		FROM XMLTABLE('/ArticleBOMFilterCollection/ArticleBOMFilter'
			PASSING XMLTYPE(p_params)
			COLUMNS
				F_GUID varchar2(50 BYTE) PATH 'F_GUID',
				F_USAGE varchar2(4) PATH 'F_USAGE',
				F_DATA_CODE varchar2(30) PATH 'F_DATA_CODE',
				F_TEXT_CODE varchar2(8) PATH 'F_TEXT_CODE',
				F_IS_NUMERIC number(1,0) PATH 'F_IS_NUMERIC',
				F_COMPARISON varchar2(50) PATH 'F_COMPARISON',
				F_VALUE nvarchar2(4000) PATH 'F_VALUE',
				F_VALUE_TARGET varchar2(50) PATH 'F_VALUE_TARGET',
				F_VALUE_NUMERIC number(14,8) PATH 'F_VALUE_NUMERIC',
				F_VALUE_TO nvarchar2(4000) PATH 'F_VALUE_TO',
				F_VALUE_TO_NUMERIC number(14,8) PATH 'F_VALUE_TO_NUMERIC',
				F_POLARITY number(1,0) PATH 'F_POLARITY',
				F_ARRAY XMLTYPE PATH 'F_ARRAY'
		) xt;
		
		

		
    INSERT INTO GTT_MM_INBOUND_FILTERS_ARRAY (
		  F_FILTER_GUID,
		  F_VALUE
		)
		SELECT
		  filters.F_GUID,
		  x.F_VALUE
		FROM GTT_MM_INBOUND_FILTERS filters
		  CROSS JOIN XMLTABLE ('/F_ARRAY/ENTRY'
			PASSING filters.F_ARRAY
			COLUMNS
			  F_VALUE nvarchar2(4000) PATH 'text()'
		  ) x;
		
		

		
		SELECT COUNT(*) INTO flg_hasComponentFilter FROM GTT_MM_INBOUND_FILTERS WHERE F_DATA_CODE LIKE 'Components';
		SELECT COUNT(*) INTO flg_hasProductFilter FROM GTT_MM_INBOUND_FILTERS WHERE F_DATA_CODE LIKE 'Products';
		SELECT to_number(F_VALUE) INTO flg_productSourceFilter
		FROM (
			SELECT F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_COMPARISON = 'ProductSource'
			UNION ALL SELECT CAST('-1' as nvarchar2(4000))
			FROM DUAL
		)
		WHERE rownum=1;
		SELECT COUNT(*) INTO flg_hasProductNameFilter FROM GTT_MM_INBOUND_FILTERS WHERE F_DATA_CODE = 'ProductNames';
		SELECT COUNT(*) INTO flg_hasCategoryFilter FROM GTT_MM_INBOUND_FILTERS WHERE F_USAGE = 'CATG';
		SELECT to_number(F_VALUE) INTO flg_MetricsRequested
		FROM (
			SELECT F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_COMPARISON = 'GetMetrics' UNION ALL
			SELECT CAST('0' as nvarchar2(4000))
			FROM DUAL
		)
		WHERE rownum=1;
		
		SELECT F_VALUE INTO var_singleModelFilter FROM (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS WHERE F_COMPARISON = 'ProductModel' UNION ALL SELECT NULL FROM DUAL) WHERE ROWNUM=1;
		SELECT F_VALUE INTO var_format FROM (SELECT CAST(F_VALUE as varchar2(3)) F_VALUE FROM GTT_MM_INBOUND_FILTERS WHERE F_COMPARISON = 'Format' UNION ALL SELECT CAST('MTR' as varchar2(3)) FROM DUAL) WHERE ROWNUM=1;
		SELECT F_VALUE INTO var_language FROM (SELECT CAST(F_VALUE as varchar2(2)) F_VALUE FROM GTT_MM_INBOUND_FILTERS WHERE F_COMPARISON = 'Language' UNION ALL SELECT CAST('EN' as varchar2(2)) FROM DUAL) WHERE ROWNUM=1;
		SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END INTO flg_hasPvalFilter FROM GTT_MM_INBOUND_FILTERS WHERE F_USAGE ='PVAL';
		SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END INTO flg_hasPtxtFilter FROM GTT_MM_INBOUND_FILTERS WHERE F_USAGE ='PTXT';
		

		
		SELECT F_VALUE INTO int_pageNum
		FROM (
			SELECT F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_USAGE = 'SCAL' AND F_COMPARISON = 'PageNum'
			UNION ALL
			SELECT CAST('0' as nvarchar2(1))
			FROM DUAL
		)
		WHERE rownum=1;
		SELECT F_VALUE INTO int_rows
		FROM (
			SELECT F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_USAGE = 'SCAL' AND F_COMPARISON='Rows'
			UNION ALL
			SELECT CAST('0' as nvarchar2(1))
			FROM DUAL
		)
		WHERE rownum=1;
		SELECT F_VALUE INTO dec_depth
		FROM (
			SELECT F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_USAGE = 'SCAL' AND F_COMPARISON = 'Depth'
			UNION ALL
			SELECT CAST('0' as nvarchar2(1))
			FROM DUAL
		)WHERE rownum=1;
		SELECT F_VALUE INTO flg_rootOnly
		FROM (
			SELECT F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_USAGE = 'SCAL' AND F_COMPARISON = 'RootOnly'
			UNION ALL
			SELECT CAST('0' as nvarchar2(1))
			FROM DUAL
		)WHERE rownum=1;
		SELECT F_VALUE INTO int_roleID
		FROM (
			SELECT
			CASE
				WHEN F_VALUE IS NULL THEN NULL
				ELSE CAST(F_VALUE as int)
			END F_VALUE
			FROM GTT_MM_INBOUND_FILTERS
			WHERE F_COMPARISON = 'RoleID'
		);
		

		
		SELECT CASE WHEN flg_productSourceFilter IS NULL THEN -1 ELSE flg_productSourceFilter END INTO flg_productSourceFilter FROM DUAL;
		SELECT CASE WHEN flg_metricsRequested IS NULL THEN -1 ELSE flg_metricsRequested END INTO flg_metricsRequested FROM DUAL;
		

		
		SELECT CASE
			WHEN COALESCE(int_rows,0) = 0 THEN 0
			WHEN COALESCE(int_pageNum,0)<= 1 THEN 1
			ELSE (int_pageNum * COALESCE(int_rows, 0)) - COALESCE(int_rows,0)
		END INTO int_rowStart
		FROM DUAL;
		SELECT CASE
			WHEN COALESCE(int_rows,0) = 0 THEN
			  0
			ELSE int_rowStart + int_rows
		END INTO int_rowEnd FROM DUAL;
		

		
		SELECT COUNT(*) INTO int_numFilters
		FROM GTT_MM_INBOUND_FILTERS
		WHERE F_USAGE IN ('PVAL','PTXT','LIST','CATG', 'RCAT','REGP')
		  AND F_COMPARISON NOT IN('ArticleBOMColumnConfig')
		  AND F_DATA_CODE NOT IN ('Components');
		

		
		SELECT
			CASE
				WHEN COUNT(*) > 0 THEN 1
				ELSE 0
			END
			INTO flg_hasAnyRecursiveLookupFltrs
				FROM GTT_MM_INBOUND_FILTERS
				WHERE F_USAGE = 'LIST'
			AND F_DATA_CODE IN ('ProductsWithCas', 'ProductsWithComp', 'ProductsWithInputProduct', 'ProductsWithChemNames');
		

        
        insert into TempCategoryCTE(F_CATEGORY_GUID, F_PARENT_GUID, F_PRODUCT)
        with CategoryCTE(F_CATEGORY_GUID, F_PARENT_GUID, F_PRODUCT)
        AS
        (
            SELECT
                pc.F_RECORD_GUID,
                pc.F_PARENT_GUID,
                pcs.F_PRODUCT
			FROM T_PRODUCT_CATEGORY pc
				INNER JOIN T_PRODUCT_CATEGORIES pcs ON pcs.F_CATEGORY_GUID = pc.F_RECORD_GUID
				INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_USAGE = 'CATG'
            WHERE
            (
                (
                    filters.F_POLARITY = 1
					AND
                    (
                        (
                            filters.F_COMPARISON = 'In'
                            AND
                            pc.F_RECORD_GUID IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID)

                        )
                        OR
                        (
                            filters.F_COMPARISON IN ('Matches', 'Contains','EndsWith','StartsWith')
                            AND
                            (
                                (pc.F_RECORD_GUID = filters.F_VALUE)
                            )
                        )
                    )
                )
                OR
                    filters.F_POLARITY = 0
                AND
                (
                    (
                        filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
                        AND
                        (pc.F_RECORD_GUID <> filters.F_VALUE)
                    )
                )
            )
            UNION ALL
            SELECT
                pc.F_RECORD_GUID,
                pc.F_PARENT_GUID,
                pcs.F_PRODUCT
            FROM T_PRODUCT_CATEGORY pc
                INNER JOIN T_PRODUCT_CATEGORIES pcs ON pcs.F_CATEGORY_GUID = pc.F_RECORD_GUID
                INNER JOIN CategoryCTE cte ON cte.F_CATEGORY_GUID = pc.F_PARENT_GUID
        )
        select * from CategoryCTE;

        insert into TempFilterCTE(F_ID,F_PARENT_ID,F_TYPE)
        with FilterCTE(F_ID,F_PARENT_ID,F_TYPE) AS
        (
            SELECT *
			FROM
            (
                SELECT
                    pc.F_CAS_NUMBER_COMPONENT_ID F_ID,
					F_PRODUCT F_PARENT,
					'COMPONENT' F_TYPE
				FROM T_PROD_COMP pc
                    INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_DATA_CODE IN ('ProductsWithCas','ProductsWithChemNames')
					LEFT JOIN
                    (
                        SELECT
                            F_CAS_NUMBER,
                            F_COMPONENT_ID,
                            F_CHEM_NAME
						FROM T_COMPONENTS_alias
						WHERE F_language = var_language
                    ) alias ON alias.F_CAS_NUMBER = pc.F_Cas_Number AND alias.F_component_id = pc.F_Component_Id
                    WHERE
                    (
                        flg_hasAnyRecursiveLookupFltrs = 1
						AND
						(
							(
								filters.F_POLARITY = 1
								AND
                                (
                                    (
                                        filters.F_COMPARISON = 'In'
										AND
                                        (
                                            (
                                                filters.F_DATA_CODE = 'ProductsWithCas'
                                                AND
                                                pc.F_Cas_Number IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID)
                                            )
											OR
                                            (
                                                (
                                                    filters.F_DATA_CODE ='ProductsWithChemNames'
													AND
													(
                                                        pc.F_Chem_Name IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID)
														OR
                                                        alias.F_Chem_name IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID)
                                                    )
                                                )
                                            )
                                        )
                                    )
									OR
                                    (
                                        filters.F_COMPARISON IN ('Matches', 'Contains','EndsWith','StartsWith')
										AND
                                        (
                                            (
                                                filters.F_DATA_CODE = 'ProductsWithCas'
                                                AND
                                                pc.F_Cas_Number LIKE CAST(filters.F_VALUE as varchar(15)) escape '/'
                                            )
                                            OR
											(
                                                filters.F_DATA_CODE = 'ProductsWithChemNames'
												AND
												(
                                                    pc.F_Chem_Name LIKE filters.F_VALUE escape '/'
													OR
                                                    alias.F_Chem_name LIKE filters.F_VALUE escape '/'
                                                )
                                            )
                                        )
                                    )
                                )
                            )
							OR
                            (
                                filters.F_POLARITY = 0
								AND
                                (
                                    (
                                        filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
										AND
                                        (
                                            (
                                                filters.F_DATA_CODE = 'ProductsWithCas'
                                                AND
                                                pc.F_Cas_Number NOT LIKE CAST(filters.F_VALUE as varchar2(15)) escape '/'
                                            )
											OR
                                            (
                                                filters.F_DATA_CODE ='ProductsWithChemNames'
                                                AND
                                                pc.F_Chem_Name NOT LIKE CAST(filters.F_VALUE as varchar2(15)) escape '/'
                                            )
                                        )
                                    )
									OR
                                    (
                                        filters.F_COMPARISON IN ('LessThan', 'LessThen')
										AND
                                        (
                                            (
                                                filters.F_DATA_CODE = 'ProductsWithCas'
                                                AND
                                                pc.F_Cas_Number > CAST(filters.F_VALUE as varchar2(15))
                                            )
											OR
                                            (
                                                filters.F_DATA_CODE = 'ProductsWithChemNames'
                                                AND
                                                pc.F_Chem_Name > CAST(filters.F_VALUE as varchar2(15))
                                            )
                                    	)
                                	)
                            	)
							)
                        )
                	)
                    UNION ALL
					SELECT
						pc.F_CAS_NUMBER_COMPONENT_ID,
						F_PRODUCT,
						'COMPONENT'
					FROM T_PROD_COMP pc
						INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_DATA_CODE = 'ProductsWithComp'
					WHERE
                    (
                        flg_hasAnyRecursiveLookupFltrs =1
						AND
						(
							(
								filters.F_POLARITY = 1
								AND
                                (
									(
										filters.F_COMPARISON = 'In'
										AND
                                        (
                                            (F_Component_Id IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID))
                                    	)
                                	)
                                    OR
                                    (
                                        filters.F_COMPARISON IN ('Matches', 'Contains','EndsWith','StartsWith')
										AND
                                        (
                                            (F_Component_Id LIKE CAST(filters.F_VALUE as varchar2(35)) escape '/')
                                    	)
                                	)
                                    OR
                                    (
                                        filters.F_COMPARISON IN ('GreaterThan')
										AND
                                        (
                                            (F_Component_Id > CAST(filters.F_VALUE as varchar2(35)))
                                    	)
                            		)
                        		)
                    		)
							OR
                            (
                                filters.F_POLARITY = 0
								AND
                                (
                                    (
                                        filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
										AND
                                        (
                                            (filters.F_DATA_CODE = 'ProductsWithComp' AND F_Component_Id NOT LIKE CAST(filters.F_VALUE as varchar2(35)) escape '/')
                                    	)
                                    )
									OR
                                    (
                                        filters.F_COMPARISON IN ('LessThan', 'LessThen')
                                        AND
                                        (
                                            (filters.F_DATA_CODE = 'ProductsWithComp' AND F_Component_Id > CAST(filters.F_VALUE as varchar2(35)))
                                		)
                        			)
                                )
                            )
                        )
                    )
					UNION ALL
					SELECT
						F_INPUT_PRODUCT,
						F_PRODUCT,
						'PRODUCT'
					FROM T_PROD_WITH_INPUT_PROD ip
						INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_DATA_CODE=  'ProductsWithInputProduct'
					WHERE
                    (
                        flg_hasAnyRecursiveLookupFltrs = 1
						AND
                        filters.F_VALUE IS NOT NULL
						AND
						(
							(
								filters.F_POLARITY = 1
								AND
                                (
                                    (
										filters.F_COMPARISON = 'In'
										AND
                                        (
											(F_Input_Product IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID))
                                        )
                                	)
                        			OR
                                    (
										filters.F_COMPARISON IN ('Matches', 'Contains','EndsWith','StartsWith')
										AND
                                        (
											(F_Input_Product LIKE CAST(filters.F_VALUE as varchar2(50)) escape '/')
										)
                                	)
									OR
                                    (
										filters.F_COMPARISON IN ('GreaterThan')
										AND
                                        (
											(F_Input_Product > CAST(filters.F_VALUE as varchar2(50)))
										)
                                    )
                                )
                            )
                        	OR
                            (
								filters.F_POLARITY = 0
								AND
                                (
                                    (
										filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
										AND
                                        (
											(filters.F_DATA_CODE = 'ProductsWithInputProduct' AND F_Input_Product NOT LIKE CAST(filters.F_VALUE as varchar2(50)) escape '/')
										)
                                	)
									OR
                                    (
										filters.F_COMPARISON IN ('LessThan', 'LessThen')
										AND
                                        (
											(filters.F_DATA_CODE = 'ProductsWithInputProduct' AND F_Input_Product > CAST(filters.F_VALUE as varchar2(50)))
										)
                                	)
                            	)
							)
                        )
					)
				)a
				UNION ALL
				SELECT
                    F_INPUT_PRODUCT,
                    F_PRODUCT,
                    CAST('PRODUCT' as varchar2(15))
				FROM FilterCTE cte
                    INNER JOIN T_PROD_WITH_INPUT_PROD ip ON ip.F_Input_Product = cte.F_PARENT_ID
            )
            select * from FilterCTE;

		
		INSERT INTO GTT_MM_LIMIT(F_ROW_NUMBER, F_PRODUCT_ID)
        SELECT RowNum, F_PRODUCT
		FROM
            (
                SELECT row_number() over (order by f_score desc, f_product)  as F_RowNum, F_PRODUCT, F_SCORE
                FROM
                (
                    SELECT F_PRODUCT, MAX(F_SCORE) F_SCORE
                    FROM
                    (
                        SELECT p.F_PRODUCT, MAX(F_COUNT) as F_SCORE
                        FROM
                        (
						
						SELECT p.F_PRODUCT, SUM(
							pt.F_COUNT +
							pd.F_COUNT +
							filters.F_COUNT +
							cte.F_COUNT +
							categories.F_COUNT +
							alternates.F_COUNT
						) F_COUNT
						FROM T_PRODUCTS p
							INNER JOIN (
								SELECT F_PRODUCT
								FROM T_PRODUCTS
								WHERE
									(flg_productSourceFilter = -1 OR (F_PRODUCT_SOURCE=flg_productSourceFilter))
							) pAttrs on pattrs.F_Product = p.f_product
						  LEFT JOIN (
							  SELECT F_PRODUCT, COUNT(*) as F_COUNT
							  FROM T_PRODUCTS p
								INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_DATA_CODE IN (
								  'Products', 'ProductNames'
							  )
							  WHERE
								(
								  (
									filters.F_POLARITY = 1
									AND (
										(
										  filters.F_COMPARISON = 'In'
										  AND (
											  (filters.F_DATA_CODE ='Products' AND EXISTS (SELECT 1 FROM GTT_MM_INBOUND_FILTERS_ARRAY arry WHERE arry.F_FILTER_GUID = filters.F_GUID AND p.F_PRODUCT=arry.F_VALUE))
										  )
										)
										OR (
										  filters.F_COMPARISON = 'Between'
										  AND (
											  (filters.F_DATA_CODE = 'Products' AND F_PRODUCT BETWEEN filters.F_VALUE AND filters.F_VALUE_TO)
											  OR (filters.F_DATA_CODE ='ProductNames' AND F_PRODUCT_NAME BETWEEN filters.F_VALUE AND filters.F_VALUE_TO)
										  )
										)
										OR (
										  filters.F_COMPARISON IN ('Matches', 'Contains','EndsWith','StartsWith')
										  AND (
											  (filters.F_DATA_CODE = 'Products' AND F_PRODUCT LIKE filters.F_VALUE escape '/')
											  OR (filters.F_DATA_CODE = 'ProductNames' AND F_PRODUCT_NAME LIKE filters.F_VALUE escape '/')
										  )
										)
										OR (
										  filters.F_COMPARISON IN ('GreaterThan')
										  AND (
											  (filters.F_DATA_CODE = 'Products' AND F_PRODUCT > filters.F_VALUE)
											  OR (filters.F_DATA_CODE = 'ProductNames' AND F_PRODUCT_NAME > filters.F_VALUE)
										  )
										)
									)
								  )
								  OR (
									filters.F_POLARITY = 0
									AND (
										(
										  filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
										  AND (
											  (filters.F_DATA_CODE = 'Products' AND F_PRODUCT NOT LIKE filters.F_VALUE escape '/')
											  OR (filters.F_DATA_CODE ='ProductNames' AND F_PRODUCT_NAME NOT LIKE filters.F_VALUE escape '/')
										  )
										)
										OR (
										  filters.F_COMPARISON IN ('LessThan', 'LessThen')
										  AND (
											  (filters.F_DATA_CODE = 'Products' AND F_PRODUCT > filters.F_VALUE)
											  OR (filters.F_DATA_CODE = 'ProductNames' AND F_PRODUCT_NAME > filters.F_VALUE)
											)
										  )
										)
									  )
									)
							  GROUP BY F_Product

							  UNION ALL
							  SELECT F_PRODUCT,0
							  FROM T_PRODUCTS
						  ) filters ON filters.F_Product = p.F_PRODUCT
						  LEFT JOIN (
							  SELECT F_PARENT_ID, 1 F_COUNT
							  FROM TempFilterCTE

							  UNION ALL
							  SELECT F_Product, 0 F_COUNT
							  FROM T_PRODUCTS
						  ) cte ON cte.F_PARENT_ID = p.F_PRODUCT
						  LEFT JOIN (
							
							SELECT F_Product, COUNT(*) as F_COUNT
							FROM (
								SELECT pd.F_PRODUCT,
									pd.F_DATA_CODE,
									pd.F_DATA,
									'PVAL' F_USAGE,
									0 F_POLARITY
								FROM T_PROD_DATA pd

								UNION ALL
								SELECT ro.F_ENTITY_ID,
									ro.F_DATA_CODE,
									ro.F_DATA,
									ro.F_USAGE,
									ro.F_POLARITY
								FROM T_REG_OUTPUT ro
								WHERE ro.F_ENTITY_TYPE = 1
							) datapoints
							  INNER JOIN GTT_MM_INBOUND_FILTERS filters ON CAST(filters.F_USAGE as varchar(4)) = datapoints.F_USAGE
								  AND filters.F_COMPARISON <> 'ArticleBOMColumnConfig'
								  AND filters.F_USAGE NOT IN ('LIST','SCAL')
							WHERE
								filters.F_DATA_CODE = datapoints.F_DATA_CODE
								AND
								  (
									(
									  filters.F_VALUE_TARGET IS NULL
									  AND (
										  (
											filters.F_POLARITY = 1
											AND (
												(
												  filters.F_COMPARISON = 'Between'
												  AND (
													(LENGTH(F_DATA) <= 20  and CAST(replace(F_DATA,',','') AS NUMBER(32,12)) BETWEEN filters.F_VALUE_NUMERIC AND filters.F_VALUE_TO_NUMERIC)
												  )
												)
												OR (
												  filters.F_COMPARISON = 'Matches' AND filters.F_IS_NUMERIC = 1
												  AND (
													  (LENGTH(F_DATA) <= 20 AND CAST(replace(F_DATA,',','') AS NUMBER(32,12)) = filters.F_VALUE_NUMERIC)
												  )
												)
												OR (
												  filters.F_IS_NUMERIC = 0 AND filters.F_COMPARISON IN ('Matches', 'Contains','EndsWith','StartsWith')
												  AND (
													  (F_DATA LIKE filters.F_VALUE escape '/')
												  )
												)
												OR (
												  filters.F_IS_NUMERIC = 1 AND filters.F_COMPARISON IN ('GreaterThan')
												  AND (
													  LENGTH(F_DATA) <= 20  AND CAST(replace(F_DATA,',','') AS NUMBER(32,12)) > filters.F_VALUE_NUMERIC
												  )
												)
											  )


										  OR (
											filters.F_POLARITY = 0
											AND (
												(
												  filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
												  AND (
													  (F_DATA NOT LIKE filters.F_VALUE escape '/')
												  )
												)
												OR (
												  filters.F_COMPARISON IN ('LessThan', 'LessThen')
												  AND (
													  (F_DATA < filters.F_VALUE)
													)
												  )
												)
											  )
											)
										)
								  )
								  OR
								  (													(
									filters.F_VALUE_TARGET = 'POLARITY'
									AND
									  (
										(
										  filters.F_COMPARISON = 'Matches'
										  AND filters.F_VALUE = CAST(datapoints.F_POLARITY as varchar2(1))
										)
										OR
										(
										  filters.F_COMPARISON = 'NotEqual'
										  AND filters.F_VALUE <> CAST(datapoints.F_POLARITY as varchar2(1))
										)
									  )
									)
								  )
								)
							GROUP BY datapoints.F_PRODUCT

							UNION ALL
							SELECT F_PRODUCT,0
							FROM T_PRODUCTS
						  ) pd ON pd.F_Product = p.F_PRODUCT
						  LEFT JOIN (
							  
							  SELECT F_PRODUCT, F_COUNT
							  FROM (
								  
								  SELECT F_PRODUCT, F_COUNT
								  FROM (
									  SELECT
										  pt.F_PRODUCT,
										  filters.F_GUID,
										  CASE
											WHEN COUNT(*) > 0 THEN 1
											ELSE 0
										  END F_COUNT
									  FROM T_PROD_TEXT pt
										  INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_USAGE  = 'PTXT'
									  WHERE
										(
										  (
											filters.F_POLARITY = 1
											AND
											(
											  filters.F_COMPARISON = 'In'
											  AND (
											  
												(pt.F_FORMAT = var_format AND pt.F_TEXT_CODE IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID))
												OR (pt.F_FORMAT = var_format AND filters.F_ARRAY IS NULL AND pt.F_DATA_CODE LIKE filters.F_DATA_CODE escape '/')
											  )
											)
											OR (
											  filters.F_COMPARISON IN ('Matches', 'StartsWith','Contains','EndsWith')
												AND (
												   (pt.F_FORMAT = var_format AND pt.F_DATA_CODE LIKE filters.F_DATA_CODE escape '/')
													OR (pt.F_FORMAT = var_format AND pt.F_TEXT_CODE LIKE filters.F_TEXT_CODE escape '/')
												)
											)
										  )
									  )
									  GROUP BY pt.F_PRODUCT, filters.F_GUID
							  ) a

							  UNION ALL

							  SELECT F_PRODUCT, 0
							  FROM T_PRODUCTS
							) a
						  ) pt ON pt.F_Product = p.F_PRODUCT
						  LEFT JOIN (
							SELECT p.F_Product, 0 F_COUNT
							FROM T_PRODUCTS p

							UNION ALL
							SELECT cte.F_PRODUCT, 1 F_COUNT
							FROM TempCategoryCTE cte
						  ) categories ON categories.F_PRODUCT = p.F_Product
						  LEFT JOIN (
							
							SELECT pgs.F_PRODUCT, 1 F_COUNT
							FROM T_PROD_GROUPING pgs
							  INNER JOIN T_PRODUCT_GROUPS groups ON groups.F_Product_Group = pgs.F_Product_Group
							  INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_DATA_CODE LIKE 'HASALTS'
							WHERE pgs.F_IS_MASTER = filters.F_VALUE_NUMERIC
							  and groups.F_GROUP_TYPE = filters.F_VALUE_NUMERIC
							GROUP BY pgs.F_PRODUCT

							UNION ALL
							SELECT F_PRODUCT, 0 F_COUNT
							FROM T_PRODUCTS
						  ) alternates ON alternates.F_Product = p.F_PRODUCT
						GROUP BY p.F_Product
					) p

				GROUP BY p.F_Product
						) a
				
				
				INNER JOIN (
					SELECT p.F_PRODUCT F_SEC_PRODUCT, rsr.F_RIGHTS
					FROM T_PRODUCTS p
						INNER JOIN T_SEC_R_S_R rsr ON p.F_SID = rsr.F_SID
					WHERE int_roleID IS NOT NULL AND rsr.F_ROLE_ID = int_roleID

					
					UNION ALL
					SELECT p.F_PRODUCT, 2
					FROM T_PRODUCTS p
					WHERE int_roleID IS NULL
					GROUP BY p.F_PRODUCT
			  ) sec ON sec.F_SEC_Product = a.F_Product
				AND sec.F_RIGHTS IN (2,4) 
						WHERE F_SCORE >=
							CASE
								WHEN int_numFilters > 0 AND var_operator = 'AND' THEN
									int_numFilters
								WHEN int_numFilters > 0 AND var_operator = 'OR' THEN
									1
								ELSE 0
							END
							
						GROUP BY F_PRODUCT
					) b
				) limiter
				
				WHERE (
						(
			  (flg_metricsRequested = 1)
			  OR (
				  flg_metricsRequested = 0
				  AND (int_rowStart <= 0 OR RowNum >= int_rowStart)
				  AND (int_rowEnd <= 0 OR ROWNUM < int_rowEnd)
			  )
						)
					)
					AND (
				(
				  flg_hasProductFilter=1 
				)
				OR (
				  flg_hasProductFilter = 0 AND flg_hasComponentFilter = 0 
			  )
					);

				SELECT MAX(F_ROW_NUMBER) INTO int_totalResults
				FROM GTT_MM_LIMIT;

		DBMS_output.put_line(int_totalResults);

