                    pm.F_CAS_NUMBER,
                    pm.F_Chem_Name,
                    pm.F_Percent,
                    pm.F_Date_Stamp,
                    pm.F_USER_UPDATED,
                    pm.F_Trade_Secret_Flag,
                    pm.F_Trade_Secret_Chem_Name,
                    pm.F_UNITS,
                    pm.F_SARA_Flag,
                    pm.F_Haz_Flag,
                    '0' F_WEIGHT_QTY
                FROM T_PROD_MODELS pm
            ) pc
            INNER JOIN T_COMPONENTS c ON pc.F_Cas_Number = c.F_Cas_Number AND pc.F_COMPONENT_ID = c.F_Component_Id
            INNER JOIN ComponentCTE cte ON cte.F_ID = pc.F_PRODUCT AND
            (
                (
                    cte.F_INPUT_PRODUCT_MODEL IS NULL AND pc.F_MODEL IS NULL
                )
                OR
                (
                    cte.F_INPUT_PRODUCT_MODEL IS NOT NULL AND cte.F_INPUT_PRODUCT_MODEL = pc.F_MODEL
                )
            )
            INNER JOIN
            (
                SELECT F_UNIT, F_CLASS
                FROM T_UNITS u
            ) u ON u.F_UNIT = pc.F_Units
            WHERE
            (
                flg_hasComponentFilter = 0
                OR
                (
                    flg_hasComponentFilter = 1
                    AND EXISTS
                    (
                        SELECT 1
                        FROM GTT_MM_INBOUND_FILTERS filters
                        WHERE filters.F_DATA_CODE = 'Components'
                        AND filters.F_VALUE = CAST((c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER) as varchar(55))
                    )
                )
            )
        )
        select * from ComponentCTE;

				
		INSERT INTO GTT_MM_STRUCTURE (
                F_ENTITY_HASH,
                F_ID,
                F_ENTITY_GUID,
                F_NAME,
                F_PARENT_ID,
                F_PARENT_HASH,
                F_ENTITY_TYPE,
                F_QTY,
                F_DEPTH,
                F_DATE_STAMP,
                F_USER_UPDATED,
                F_TRADE_SECRET_FLAG,
                F_UNITS,
                F_SARA_FLAG,
                F_TRADE_SECRET_CHEM_NAME,
                F_HAZ_FLAG,
                F_LINKAGE_TYPE,
                F_LINKAGE_ACTIVE,
                F_LINKAGE_GUID,
                F_LINKED_ENTITY_GUID,
                F_COMP_CAS_DELIM,
                F_WEIGHT_QTY,
                F_COMPONENT_MEASUREMENT_TYPE,
                F_ROLLUP_QUEUE_STATUS,
                F_ENTITY_STATUS,
                F_MODEL,
                F_INPUT_PRODUCT_MODEL,
                F_IN_MM_GROUP,
                F_IS_MM_MASTER,
                F_RIGHTS,
                F_CHAIN_ID,
                F_PARENT_CHAIN_ID
		)
		SELECT DISTINCT
            utl_raw.cast_to_raw(ORA_HASH(cte.F_ID)) F_ENTITY_HASH,
			cte.F_ID,
			cte.F_ENTITY_GUID,
			COALESCE(F_ALIAS_CHEM_NAME, cte.F_NAME), 
			cte.F_PARENT_ID,
			utl_raw.cast_to_raw(ORA_HASH(cte.F_PARENT_ID)) F_PARENT_HASH,
			cte.F_ENTITY_TYPE,
			cte.F_QTY,
			cte.F_DEPTH,
			cte.F_DATE_STAMP,
			cte.F_USER_UPDATED,
			cte.F_TRADE_SECRET_FLAG,
			cte.F_UNITS,
			cte.F_SARA_FLAG,
			cte.F_TRADE_SECRET_CHEM_NAME,
			cte.F_HAZ_FLAG,
			cte.F_LINKAGE_TYPE,
			cte.F_LINKAGE_ACTIVE,
			cte.F_LINKAGE_GUID,
			cte.F_LINKED_ENTITY_GUID,
			cte.F_COMP_CAS_DELIM,
			cte.F_WEIGHT,
			cte.F_COMPONENT_MEASUREMENT_TYPE,
			COALESCE(q.F_STATUS,1),
			cte.F_ENTITY_STATUS,
			cte.F_MODEL,
			cte.F_INPUT_PRODUCT_MODEL,
			CASE
                WHEN groups.F_Product IS NULL THEN 0
                ELSE groups.F_GROUP_TYPE
			END F_IN_MM_GROUP,
			CASE
                WHEN groups.F_PRODUCT IS NULL THEN 0
                ELSE groups.F_IS_MASTER
			END F_IS_MM_MASTER,
			COALESCE(F_RIGHTS, 0) F_RIGHTS, 
			F_CHAIN_ID,
			F_PARENT_CHAIN_ID
		FROM (
            SELECT cte.* FROM TempComponentCTE cte
            UNION ALL
                
				SELECT
                    c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER,
                    NULL,
                    c.F_CHEM_NAME,
                    NULL,
                    'COMPONENT',
                    100.0,
                    0.0,
                    c.f_date_stamp,
                    c.f_user_updated,
                    0,
                    '',
                    0,
                    c.F_Trade_Secret_Name,
                    0,
                    'None',
                    1,
                    NULL,
                    NULL,
                    c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER,
                    '0',
                    0,				
                    c.F_STATUS,		
                    NULL,
                    NULL,
                    CAST(utl_raw.cast_to_raw(ORA_HASH(c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER)) as raw(16)),
                    NULL,
                    c.F_Cas_Number,
                    c.F_Component_Id,
                    c.F_SID
                FROM T_COMPONENTS c
                    INNER JOIN (
                        SELECT c.F_CAS_NUMBER_COMPONENT_ID, F_COMPONENT_ID,F_CAS_NUMBER, F_CHEM_NAME
						FROM T_COMPONENTS c
                            inner JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_DATA_CODE LIKE 'Components'
                        WHERE
                        (
                            flg_hasComponentFilter = 1
                            AND
                            flg_hasProductFilter = 0
							AND
							(
                                filters.F_POLARITY = 1
								AND
                                (
                                    (
                                        filters.F_COMPARISON = 'In'
										AND
                                        (
                                            c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER IN (SELECT F_VALUE FROM GTT_MM_INBOUND_FILTERS_ARRAY WHERE F_FILTER_GUID = filters.F_GUID)
                                        )
                                    )
									OR
                                    (
                                        filters.F_COMPARISON IN ('Matches','Contains', 'StartsWith','EndsWith')
										AND 
                                        (c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER = CAST(filters.F_VALUE as varchar(50)))
                                    )
                                )
                            )
							OR
                            (
                                filters.F_POLARITY = 0
								AND
                                (
                                    (
                                        filters.F_COMPARISON IN ('NotEqual','DoesNotContain')
										AND 
                                        (c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER <> CAST(filters.F_VALUE as varchar2(50)))
                                    )
                                )
                            )
                        )
                    ) filtered ON filtered.F_CAS_NUMBER_COMPONENT_ID = c.F_CAS_NUMBER_COMPONENT_ID
                ) cte
            LEFT JOIN (
                SELECT F_SID, F_RIGHTS
                FROM T_SEC_R_S_R
                WHERE int_roleID IS NOT NULL AND int_RoleID = F_ROLE_ID
        ) rights ON rights.F_SID = cte.F_SID
        LEFT JOIN (
            
			SELECT c.F_CAS_NUMBER F_ALIAS_CAS_NUMBER, c.F_COMPONENT_ID F_ALIAS_COMPONENT_ID, COALESCE(ca.F_CHEM_NAME, c.F_CHEM_NAME) F_ALIAS_CHEM_NAME
            FROM T_COMPONENTS c
			LEFT JOIN T_COMPONENTS_alias ca ON ca.F_CAS_NUMBER = c.F_Cas_Number
                AND ca.F_component_id = c.F_Component_Id
				AND ca.F_language LIKE var_language
        ) comp_names ON comp_names.F_ALIAS_CAS_NUMBER = cte.F_CAS_NUMBER
		AND comp_names.F_ALIAS_COMPONENT_ID = cte.F_COMPONENT_ID
		LEFT JOIN (
            
			
			
			SELECT F_ENTITY_IDENTIFIER, F_ENTITY_TYPE, 2 F_STATUS 
			FROM T_ROLLUP_QUEUE
			WHERE F_ENTITY_TYPE = 1 
            AND F_STATUS IN (0,2)
			GROUP BY F_ENTITY_IDENTIFIER, F_ENTITY_TYPE
        ) q ON q.F_ENTITY_IDENTIFIER = cte.F_ID
		AND 'PRODUCT' LIKE cte.F_ENTITY_TYPE
        LEFT JOIN (
            
			SELECT pgs.F_PRODUCT, MAX(CAST(pg.F_GROUP_TYPE as int)) F_GROUP_TYPE, MAX(CAST(pgs.F_IS_MASTER as int)) F_IS_MASTER
			FROM T_PRODUCT_GROUPS pg
			INNER JOIN T_Prod_Grouping pgs ON pg.F_Product_Group = pgs.F_Product_Group
			WHERE pg.F_GROUP_TYPE = 1
			GROUP BY pgs.F_Product
        ) groups ON groups.F_Product = cte.F_ID
		AND 'PRODUCT' LIKE cte.F_ENTITY_TYPE;

		
        INSERT INTO GTT_MM_DATA
		SELECT *
		FROM (
            
			SELECT
                s.F_ENTITY_HASH,
                'ProdData' F_DATA_TYPE,
                pd.F_DATA_CODE,
                NULL F_TEXT_CODE,
                pd.F_DATA,
                NULL F_FORMAT
            FROM T_PROD_DATA pd
            INNER JOIN GTT_MM_STRUCTURE s ON s.F_ID = pd.F_Product
			INNER JOIN (
                SELECT F_DATA_CODE
				FROM GTT_MM_INBOUND_FILTERS filters
				WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
				AND (F_USAGE = 'PVAL' OR F_USAGE = 'PTXT')
            ) rdc ON rdc.F_DATA_CODE = pd.F_DATA_CODE
			WHERE s.F_ENTITY_TYPE = 'PRODUCT'
			UNION ALL
			
            SELECT
                s.F_ENTITY_HASH,
				'ProdText' F_DATA_TYPE,
				pt.F_DATA_CODE,
				pt.F_TEXT_CODE,
				NULL F_DATA,
				F_FORMAT
            FROM T_PROD_TEXT pt
			INNER JOIN GTT_MM_STRUCTURE s ON s.F_ID = pt.F_Product
			INNER JOIN (
                SELECT F_DATA_CODE, F_TEXT_CODE
				FROM GTT_MM_INBOUND_FILTERS
				WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
				AND (F_USAGE = 'PTXT' OR F_USAGE = 'PVAL')
            ) rdc ON rdc.F_DATA_CODE = pt.F_DATA_CODE
			WHERE s.F_ENTITY_TYPE = 'PRODUCT'
			UNION ALL
            
            SELECT
                s.F_ENTITY_HASH,
				'CompData' F_DATA_TYPE,
				cd.F_DATA_CODE,
				NULL F_TEXT_CODE,
				cd.F_DATA,
				NULL F_FORMAT
            FROM T_COMP_DATA cd
			INNER JOIN GTT_MM_STRUCTURE s ON s.F_ID = (cd.F_COMPONENT_ID || '' || cd.F_CAS_NUMBER)
			INNER JOIN (
                SELECT F_DATA_CODE
				FROM GTT_MM_INBOUND_FILTERS
				WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
				AND (F_USAGE = 'CVAL' OR F_USAGE = 'CTXT')
                UNION ALL
				SELECT rg.F_REGULATION
				FROM T_Reg_Grouping rg
				INNER JOIN T_REGULATIONS r ON r.F_Regulation = rg.F_Regulation
				INNER JOIN GTT_MM_INBOUND_FILTERS filters ON filters.F_COMPARISON = 'ArticleBOMColumnConfig'
				WHERE
                (
                    (
                        filters.F_USAGE = 'RCAT' AND rg.F_Reg_Category = filters.F_DATA_CODE
                    )
					OR
                    (
                        filters.F_USAGE = 'REGC' AND r.F_Regulation = filters.F_DATA_CODE
                    )
                )
            ) rdc ON rdc.F_DATA_CODE = cd.f_DATA_CODE
            WHERE s.F_ENTITY_TYPE LIKE 'COMPONENT'
			UNION ALL
            
			SELECT
                s.F_ENTITY_HASH,
				'CompText' F_DATA_TYPE,
				ct.F_DATA_CODE,
				ct.F_TEXT_CODE,
				NULL F_DATA,
				ct.F_Format
            FROM T_COMP_TEXT ct
			INNER JOIN GTT_MM_STRUCTURE s ON s.F_ID = ct.F_CAS_NUMBER_COMPONENT_ID
			INNER JOIN
            (
                SELECT F_TEXT_CODE
				FROM GTT_MM_INBOUND_FILTERS
				WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
				AND (F_USAGE = 'CTXT' OR F_USAGE = 'CVAL')
            ) rdc ON rdc.F_TEXT_CODE = ct.F_TEXT_CODE
			WHERE s.F_ENTITY_TYPE LIKE 'COMPONENT'
			UNION ALL
            
			SELECT
                s.F_ENTITY_HASH,
                'FormData' F_DATA_TYPE,
                fd.F_DATA_CODE,
                NULL F_TEXT_CODE,
                fd.F_DATA,
                NULL F_FORMAT
            FROM T_FORM_DATA fd
            INNER JOIN GTT_MM_STRUCTURE s ON s.F_PARENT_ID = fd.F_PRODUCT
                AND s.F_ID = fd.F_CAS_NUMBER_COMPONENT_ID
            INNER JOIN
            (
                SELECT F_DATA_CODE
				FROM GTT_MM_INBOUND_FILTERS
				WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
				AND (F_USAGE = 'FVAL' OR F_USAGE = 'FTXT')
            ) rdc ON rdc.F_DATA_CODE = fd.F_DATA_CODE
    		UNION ALL
            
			SELECT
                s.F_ENTITY_HASH,
				'FormText' F_DATA_TYPE,
				ft.F_DATA_CODE,
				ft.F_Text_Code,
				NULL F_DATA,
				F_Format
            FROM T_FORM_TEXT ft
			INNER JOIN GTT_MM_STRUCTURE s ON s.F_PARENT_ID = ft.F_PRODUCT
                AND s.F_ID = ft.F_CAS_NUMBER_COMPONENT_ID
            INNER JOIN
            (
                SELECT F_TEXT_CODE
				FROM GTT_MM_INBOUND_FILTERS
				WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
				AND (F_USAGE = 'FTXT' OR F_USAGE = 'PVAL')
            ) rdc ON rdc.F_TEXT_CODE = ft.F_TEXT_CODE
        ) data
		
		WHERE F_DATA_TYPE NOT LIKE '%Text'
		OR
        (
            F_DATA_TYPE LIKE '%Text'
            AND
            F_FORMAT = var_format
        );
		
		

		
