			  LEFT JOIN T_Reg_Categories rc ON rc.F_Reg_Category = rg.F_Reg_Category
			  INNER JOIN (
				  SELECT F_DATA_CODE, F_USAGE
				  FROM GTT_MM_INBOUND_FILTERS
				  WHERE F_COMPARISON = 'ArticleBOMColumnConfig'
