				OR (rdc.F_USAGE = 'RCAT' AND rdc.F_DATA_CODE = rg.F_Reg_Category)
				OR (rdc.F_DATA_CODE = '*')
			)
		WHERE a.F_ENTITY_TYPE = 'PRODUCT';

		
		OPEN p_rs_reg_comps FOR
			SELECT
				F_ENTITY_HASH,
				rc.*,
				a.F_NAME F_CHEM_NAME
			FROM t_reg_components rc
			LEFT JOIN (
				SELECT *
				FROM (
					SELECT rg.F_Reg_Category, rg.F_Regulation
