			pt.F_PHRASE,
			pl.F_TEXT_TYPE
				FROM T_PHRASE_LINKAGE pl
			INNER JOIN T_PHRASE_TRANSLATIONS pt ON pl.F_PHRASE_ID = pt.F_PHRASE_ID
				WHERE pt.F_LANGUAGE = 'EN'
					AND
		  (
							(
								F_DATA_CODE IN (
					SELECT F_DATA_CODE
					FROM GTT_MM_DATA
								)
				AND pl.F_TEXT_TYPE =1
							)
						OR
							(
								F_TEXT_CODE IN (
					SELECT F_TEXT_CODE
					FROM GTT_MM_DATA
								)
							)
					);


		
		OPEN p_rs_hashes FOR
				SELECT
			s.F_ENTITY_TYPE,
			s.F_ENTITY_HASH,
			p.F_DATA_HASH,
			p.F_TEXT_HASH,
			p.F_COMP_HASH,
			p.F_REG_HASH,
			p.F_INPUT_HASH
				FROM T_PRODUCTS p
			INNER JOIN (
			  SELECT F_ENTITY_TYPE, F_ID, F_ENTITY_HASH
			  FROM GTT_MM_STRUCTURE
			  GROUP BY F_ENTITY_TYPE, F_ID, F_ENTITY_HASH
			) s ON s.F_ID = p.F_Product
				WHERE s.F_ENTITY_TYPE LIKE 'PRODUCT'

				UNION ALL
				SELECT
			s.F_ENTITY_TYPE,
			s.F_ENTITY_HASH,
			c.F_DATA_HASH,
			c.F_TEXT_HASH,
			NULL,
			NULL,
			NULL
				FROM T_COMPONENTS c
			INNER JOIN (
			  SELECT F_ENTITY_TYPE, F_ID, F_ENTITY_HASH
			  FROM GTT_MM_STRUCTURE
			  GROUP BY F_ENTITY_TYPE, F_ID, F_ENTITY_HASH
			) s ON s.F_ID = (c.F_COMPONENT_ID || '' || c.F_CAS_NUMBER)
				WHERE s.F_ENTITY_TYPE LIKE 'COMPONENT';

				
		OPEN p_rs_metrics FOR
				SELECT
					int_pageNum F_PAGE,
					int_rows F_ROWS,
					int_totalResults F_TOTAL_RESULTS,
		  int_numFilters F_NUM_FILTERS,
		  var_singleModelFilter F_MODEL_FILTER,
		  dec_depth F_DEPTH
		FROM dual;


		DELETE FROM GTT_MM_LIMIT;
		DELETE FROM GTT_MM_STRUCTURE;
		DELETE FROM GTT_MM_DATA;
		DELETE FROM GTT_MM_INBOUND_FILTERS;
		DELETE FROM GTT_MM_INBOUND_FILTERS_ARRAY;

        DELETE FROM TempComponentCTE;
        DELETE FROM TEMP_TFS_106067;
        DELETE FROM TEMPSITESUPPLIERCTE;
        DELETE FROM TempAlternatesBOMCTE;
        DELETE FROM TempAlternatesCTE;
        DELETE FROM TempProductCTE;
        DELETE FROM TempFilterCTE;
        DELETE FROM TempCategoryCTE;

		COMMIT;
END;

