--------------------------------------------------------
--  DDL for Procedure SVT_CSCL
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "WERCS"."SVT_CSCL" (
	v_FINANCIAL_YEAR IN NUMBER,
	v_START_DATE IN DATE,
	v_END_DATE IN DATE,
	v_LEGAL_ENTITY IN VARCHAR2,
	v_CSCL_CLASS IN VARCHAR2,
    p_rs_results OUT SYS_REFCURSOR
)
IS
    v_REPORT_YEAR NUMBER(4,0);
	v_REPORT_PERIOD_START VARCHAR2(10);
	v_REPORT_PERIOD_END   VARCHAR2(10);
    v_REPORT_CSCL_CLASS VARCHAR2(10);

    v_GEN_REG_DC VARCHAR2(8 BYTE);
	v_PRI_REG_DC VARCHAR2(8);
	v_MON_REG_DC VARCHAR2(8);
	v_METINUM_DC VARCHAR2(8);
	v_PCN_DC VARCHAR2(8);
	v_PLN_DC VARCHAR2(8);
	v_MCN_DC VARCHAR2(8);
	v_MLN_DC VARCHAR2(8);
	v_SALT_DC VARCHAR2(8);
	v_COX_DC VARCHAR2(8);

    v_legal_entities varchar2(4000);
    v_category_id number(10,0);

BEGIN
v_REPORT_YEAR := CASE
        WHEN v_FINANCIAL_YEAR IS NOT NULL AND v_FINANCIAL_YEAR > 1000 THEN v_FINANCIAL_YEAR
		WHEN EXTRACT(MONTH FROM SYSDATE) <= 3 THEN EXTRACT(YEAR FROM SYSDATE) - 1
		ELSE EXTRACT(MONTH FROM SYSDATE)
    END;

	v_REPORT_PERIOD_START := TO_CHAR(v_REPORT_YEAR) || '/04/01';
	v_REPORT_PERIOD_END   := TO_CHAR(v_REPORT_YEAR + 1) || '/03/31';

    /* Reporting Period First Date Override */
	IF v_START_DATE IS NOT NULL THEN
		v_REPORT_PERIOD_START := v_start_date;
	END IF;

	/* Reporting Period Last Date Override */
	IF v_END_DATE IS NOT NULL THEN
		v_REPORT_PERIOD_END := v_END_DATE;
	END IF;

	/* Filter by Legal Entity. A comma-delimited list of legal entity ID(s) to be displayed on the CSCL report */
	IF v_LEGAL_ENTITY IS NULL OR v_LEGAL_ENTITY = '' THEN
		-- By default we will automatically get a list of legal entities from all SVT queries that have the query category of "CSCL"
        /*
		SELECT @LEGAL_ENTITY = STUFF((
			SELECT DISTINCT ',' + LE.DATA
                FROM T_SVT_REPORTS R
                INNER JOIN T_SVT_REPORT_CATEGORIES RC ON R.F_REPORT_CATEGORY_ID=RC.F_RECORD_ID
                CROSS APPLY (SELECT DATA FROM DBO.SPLIT(R.F_LEGAL_ENTITIES, ',')) LE
                    WHERE RC.F_NAME='CSCL'
                    FOR XML PATH('')
                ), 1, 1, '')
        */
        select listagg(column_value, ',') within group (order by column_value)
        into v_legal_entities
        from (
            select unique column_value from TABLE(
                fnsplit(
                    (
                        select LISTAGG(f_LEGAL_ENTITIES, ',') WITHIN GROUP (ORDER BY f_LEGAL_ENTITIES)
                        from T_SVT_REPORTS r
                        inner join T_SVT_REPORT_CATEGORIES RC ON R.F_REPORT_CATEGORY_ID=RC.F_RECORD_ID
                        WHERE RC.F_NAME='CSCL'
                    ),
                    ',')
            )
        );

	END IF;

	/* Filter by CSCL Class. A comma-delimited list of capital letter(s) that represent the CSCL Class to be displayed on the report. G=General, P=Priority, M=Monitoring */
    v_REPORT_CSCL_CLASS := v_CSCL_CLASS;
	IF v_REPORT_CSCL_CLASS IS NULL OR v_REPORT_CSCL_CLASS = '' THEN
		v_REPORT_CSCL_CLASS := 'G,M,P';
    END IF;

    SELECT NVL(F_VALUE, '') INTO v_GEN_REG_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_NEW_GENERAL_REGULATION';
    SELECT NVL(F_VALUE, '') INTO v_PRI_REG_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_NEW_PRIORITY_REGULATION';
	SELECT NVL(F_VALUE, '') INTO v_MON_REG_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_NEW_MONITORING_REGULATION';

    --v_METINUM_DC := COALESCE((SELECT NVL(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_METI_DATACODE'), 'ENCSNUM');
    SELECT NVL(F_VALUE, '') INTO v_METINUM_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_METI_DATACODE';

    --	v_PCN_DC := COALESCE((SELECT NULLIF(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_PRIORITY_SUBSTANCE_CONTROL_NUMBER_DATACODE'), 'CSCLPCN');
    SELECT NVL(F_VALUE, '') INTO v_PCN_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_PRIORITY_SUBSTANCE_CONTROL_NUMBER_DATACODE';

    --	v_PLN_DC := COALESCE((SELECT NULLIF(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_PRIORITY_LISTED_NAME_DATACODE'), 'CSCLPLN');
    SELECT NVL(F_VALUE, '') INTO v_PLN_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_PRIORITY_LISTED_NAME_DATACODE';

    --	v_MCN_DC := COALESCE((SELECT NULLIF(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_MONITORING_SUBSTANCE_CONTROL_NUMBER_DATACODE'), 'CSCLMCN');
    SELECT NVL(F_VALUE, '') INTO v_MCN_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_MONITORING_SUBSTANCE_CONTROL_NUMBER_DATACODE';

    --	v_MLN_DC := COALESCE((SELECT NULLIF(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_MONITORING_LISTED_NAME_DATACODE'), 'CSCLMLN');
    SELECT NVL(F_VALUE, '') INTO v_MLN_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_MONITORING_LISTED_NAME_DATACODE';

    --	v_SALT_DC := COALESCE((SELECT NULLIF(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_SALT_INDICATOR_DATACODE'), 'CSCLSALT');
    SELECT NVL(F_VALUE, '') INTO v_SALT_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_SALT_INDICATOR_DATACODE';

    --	v_COX_DC := COALESCE((SELECT NULLIF(F_VALUE, '') FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_COX_INDICATOR_DATACODE'), 'CSCLCOX');
    SELECT NVL(F_VALUE, '') INTO v_COX_DC FROM T_LOOKUP WHERE F_PARENT='SVT' AND F_KEY='CSCL_COX_INDICATOR_DATACODE';

	/* ====== MAIN LOGIC ====== */
	/* Step 1 : Get all volume data within the reporting period along with its related information (such as component functions, CSCL classes, and METI number) and perform in-memory conversion of the transaction records in order to make the aggregation easier */
	-- Temp table to store intermediate data

    SELECT F_RECORD_ID into v_category_id FROM WERCS.T_SVT_REPORT_CATEGORIES WHERE rownum=1 and F_NAME='CSCL' AND F_FUNCTION_DATA_CODE='CSCLF';

	INSERT INTO TEMP_SVT_CSCL (
		F_TRANS_DATE,
		F_LEGAL_ENTITY_ID,
		F_PRODUCT,
		F_CAS_NUMBER,
		F_COMPONENT_ID,
		F_QUANTITY,
		F_UOM,
		F_TRANS_TYPE,
		F_SHIPPED_FROM_COUNTRY,
		F_SHIPPED_TO_ID,
		F_SHIPPED_TO_COUNTRY,
		F_SHIPPED_TO_STATE,
		F_CSCL_USAGE_CODE,
		F_CSCL_ACTUAL_USE_INFO,
        T,
		CSCLF_TX,
		F_INCLUDE_PURCH,
		F_INCLUDE_MANU,
		F_INCLUDE_SOLD,
		F_POLYMER_INDICATOR,
		AGGREGATE_METHOD,
		F_TRANS_TYPE_CONVERTED,
		F_SHIPPED_FROM_COUNTRY_CONVERT,
		F_SHIPPED_TO_ID_CONVERTED,
		F_SHIPPED_TO_STATE_CONVERTED,
		F_SHIPPED_TO_COUNTRY_CONVERTED,
		F_CSCL_USAGE_CODE_CONVERTED
        )
    SELECT
		/* === Original Transaction Data from T_SVT_VOLUMES === */
		V.F_TRANS_DATE, V.F_LEGAL_ENTITY_ID, V.F_PRODUCT, V.F_CAS_NUMBER, V.F_COMPONENT_ID,
		V.F_QUANTITY, V.F_UOM, V.F_TRANS_TYPE,
		V.F_SHIPPED_FROM_COUNTRY, V.F_SHIPPED_TO_ID, V.F_SHIPPED_TO_COUNTRY, V.F_SHIPPED_TO_STATE,
		V.F_CSCL_USAGE_CODE, V.F_CSCL_ACTUAL_USE_INFO,

        /* === Related information grabbed from other tables === */
		CSCL.T,
		FT.F_TEXT_CODE, --[CSCLF_TX]
		CF.F_INCLUDE_PURCH, CF.F_INCLUDE_MANU, CF.F_INCLUDE_SOLD, CF.F_POLYMER_INDICATOR,

		/* === Converted Transaction Data for Actual Grouping  === */
		/* X Coefficient (the aggregate method : add, deduct, or ignore) */
		CASE
			WHEN V.F_TRANS_TYPE = 1 AND COALESCE(V.F_SHIPPED_FROM_COUNTRY, '') != 'JPN' AND CF.F_INCLUDE_PURCH = 1 THEN 1	-- I-15, P-15, W-15: Imports
			WHEN V.F_TRANS_TYPE = 2 AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '')    = 'JPN' AND CF.F_INCLUDE_MANU  = 1 THEN 1	-- I-21, P-21, W-21: Domestic manufactures
			WHEN V.F_TRANS_TYPE = 3 THEN
                CASE
                    WHEN V.F_SHIPPED_TO_COUNTRY = 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') = 'DEDUCTION' THEN -1	-- I-27, P-27, W-27: Deduction
                    WHEN CF.F_INCLUDE_SOLD = 1 THEN 1															    -- I-23, P-23, W-23, I-25, P-25, W-25: Domestic  Shipments
					ELSE 0
                END
          	ELSE 0
		END AGGREGATE_METHOD,

		/* Converted Transaction Type */
		CASE
			-- Important: 'Deduction' will cause volume to be deducted from the manufactured amount of the same chemical.
			--            We don't care if this results in a negative volume (it will just be ignored in the final report)
			WHEN V.F_TRANS_TYPE = 3 AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') = 'DEDUCTION' THEN 2		-- J-27, Q-27, X-27: Convert TransType from 3 to 2 to indicate which record we want to deduct the volume from
			ELSE V.F_TRANS_TYPE
		END F_TRANS_TYPE_CONVERTED,

		/* Converted FROM Country */
		CASE
			WHEN V.F_TRANS_TYPE = 1 AND CSCL.T IN ('P', 'M') AND NVL(V.F_SHIPPED_FROM_COUNTRY, '') != 'JPN' THEN V.F_SHIPPED_FROM_COUNTRY	-- R15, R16, Y15, Y16: PRIORITY  imports
			ELSE NULL		-- Null out Shipped FROM Country for other transactions
		END F_SHIPPED_FROM_COUNTRY_CONVERT,

		/* Converted Shipped To ID */
		CASE
			WHEN V.F_TRANS_TYPE = 2 AND CSCL.T = 'M' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' AND CF.F_INCLUDE_MANU  = 1 THEN V.F_SHIPPED_TO_ID									-- Z21: Domestic manufactures for MONITORING
			WHEN V.F_TRANS_TYPE = 3 AND CSCL.T = 'M' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') = 'DEDUCTION' THEN V.F_SHIPPED_TO_ID	-- Z27: Deduction for MONITORING
			ELSE NULL		-- Null out Shipped TO ID for other transactions
		END F_SHIPPED_TO_ID_CONVERTED,

		/* Converted Shipped To State */
		CASE
			WHEN V.F_TRANS_TYPE = 2 AND CSCL.T = 'P' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' THEN V.F_SHIPPED_TO_STATE	-- T21, T22: Domestic manufactures for PRIORITY
			WHEN V.F_TRANS_TYPE = 2 AND CSCL.T = 'M' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' THEN V.F_SHIPPED_TO_STATE	-- AA21, AA22: Domestic manufactures for MONITORING
			WHEN V.F_TRANS_TYPE = 3 AND CSCL.T = 'P' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') != 'DEDUCTION' THEN V.F_SHIPPED_TO_STATE	-- T25, T26: Domestic Shipment for PRIORITY
			WHEN V.F_TRANS_TYPE = 3 AND CSCL.T = 'P' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '')  = 'DEDUCTION' THEN V.F_SHIPPED_TO_STATE	-- T27: Deduction for PRIORITY
			WHEN V.F_TRANS_TYPE = 3 AND CSCL.T = 'M' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') != 'DEDUCTION' THEN V.F_SHIPPED_TO_STATE	-- AA25, AA26: Domestic Shipment for MONITORING
			WHEN V.F_TRANS_TYPE = 3 AND CSCL.T = 'M' AND COALESCE(V.F_SHIPPED_TO_COUNTRY, '') = 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') = 'DEDUCTION' THEN V.F_SHIPPED_TO_STATE	-- AA27: Domestic Shipment for MONITORING + Deduction
			ELSE NULL		-- Null out Shipped TO State for other transactions
		END F_SHIPPED_TO_STATE_CONVERTED,

		/* Converted Shipped To Country */
		CASE
			WHEN V.F_TRANS_TYPE IN (2, 3) AND CSCL.T IN ('P', 'M') AND NVL(V.F_SHIPPED_TO_COUNTRY, '') != 'JPN' AND NVL(V.F_CSCL_ACTUAL_USE_INFO, '') != 'DEDUCTION' THEN V.F_SHIPPED_TO_COUNTRY	-- U19, U20, U23, U24, AB19, AB20, AB23, AB24: Overseas manufacture  for PRIORITY 
			ELSE NULL		-- Null out Shipped TO Country for other transactions
		END F_SHIPPED_TO_COUNTRY_CONVERTED,

		/* Converted CSCL Usage Code */
		CASE
			WHEN NVL(V.F_CSCL_ACTUAL_USE_INFO, '') = 'DEDUCTION' THEN NULL		-- O27, V27, AC27: F_CSCL_ACTUAL_USE_INFO should always be nulled out for Deductions
			WHEN V.F_TRANS_TYPE = 3 THEN
				CASE
					WHEN NVL(V.F_SHIPPED_TO_COUNTRY, '') != 'JPN' THEN
						CASE
							WHEN CSCL.T='P' OR CSCL.T='M' THEN '199-a'	-- V23, V24, AC23, AC24: Exports for Priority 
							ELSE '199'									-- O23, O24: Exports for General
						END
					ELSE
						CASE
							WHEN CSCL.T='P' OR CSCL.T='M' THEN V.F_CSCL_USAGE_CODE						-- V25-26, AC25-26: Domestic shipments for Priority 
							--ELSE (SELECT DATA FROM dbo.split(V.F_CSCL_USAGE_CODE, '-') WHERE ID=1)	        -- O25-26: Domestic shipments for General
                            ELSE (SELECT * FROM table(fnsplit(V.F_CSCL_USAGE_CODE, '-')) WHERE ROWNUM=1)	        -- O25-26: Domestic shipments for General
						END
				END
			ELSE NULL			-- Null out Usage Code for other transactions
		END F_CSCL_USAGE_CODE_CONVERTED

    FROM (
		SELECT
			F_TRANS_DATE, NVL(F_LEGAL_ENTITY_ID, '') F_LEGAL_ENTITY_ID, F_PRODUCT, F_CAS_NUMBER, F_COMPONENT_ID, F_CAS_NUMBER_COMPONENT_ID,
			F_QUANTITY, F_UOM, F_TRANS_TYPE, NULLIF(F_SHIPPED_FROM_COUNTRY, '') F_SHIPPED_FROM_COUNTRY, NVL(F_SHIPPED_TO_ID, '') F_SHIPPED_TO_ID, NVL(F_SHIPPED_TO_COUNTRY, '') F_SHIPPED_TO_COUNTRY,
			NVL(F_SHIPPED_TO_STATE, '') F_SHIPPED_TO_STATE, NVL(LOWER(F_CSCL_USAGE_CODE), '') F_CSCL_USAGE_CODE, NVL(F_CSCL_ACTUAL_USE_INFO, '') F_CSCL_ACTUAL_USE_INFO
		FROM WERCS.T_SVT_VOLUMES V1
	) V

	/* Component Functions */
    LEFT JOIN (
		SELECT FT.F_PRODUCT, FT.F_CAS_NUMBER_COMPONENT_ID, FT.F_DATA_CODE, FT.F_TEXT_CODE, PT.F_PHRASE FROM WERCS.T_FORM_TEXT FT
		LEFT JOIN WERCS.T_PHRASE_LINKAGE PL ON FT.F_DATA_CODE=PL.F_DATA_CODE AND FT.F_TEXT_CODE=PL.F_TEXT_CODE
		LEFT JOIN WERCS.T_PHRASE_TRANSLATIONS PT ON PL.F_PHRASE_ID=PT.F_PHRASE_ID AND PT.F_LANGUAGE='EN'
		WHERE FT.F_DATA_CODE='CSCLF'
	) FT ON
		V.F_PRODUCT=FT.F_PRODUCT
		AND V.F_CAS_NUMBER_COMPONENT_ID=FT.F_CAS_NUMBER_COMPONENT_ID

	/* Component Function Definitions */
	LEFT JOIN T_SVT_FUNCTIONS CF ON
		CF.F_TEXT_CODE=FT.F_TEXT_CODE
		AND CF.F_CATEGORY_ID in (
			SELECT F_RECORD_ID FROM T_SVT_REPORT_CATEGORIES WHERE ROWNUM=1 AND F_NAME='CSCL' AND F_FUNCTION_DATA_CODE='CSCLF'
		)

    /* CSCL Class */
	LEFT JOIN (
		SELECT DISTINCT
			F.F_CAS_NUMBER CAS,
			/* Map CSCL Class */
			COALESCE(T2.T, T3.T, T1.T) T
		FROM T_COMPONENTS F

		/* Identify Substances by CSCL Class */
		/* Important: Use the combination of F_CAS_NUMBER  to make sure we're grabbing the correct chemical. In some cases, chemicals pending METI's approval don't have CAS Numbers
					  so it is very likely that such chemicals will be given the F_CAS_NUMBER of '-' which will cause problems if we don't use F_COMPONENT_ID in conjunction when fetching records */
		LEFT JOIN (
			/* General Substances */
			SELECT CAS, 'LOLI' COMPID, 'G' T FROM LISTDATA WHERE LISTID=622 AND CAS NOT IN (SELECT CAS FROM LISTDATA WHERE LISTID IN (4598, 4589, 634))
			UNION
			SELECT R.F_CAS_NUMBER, R.F_COMPONENT_ID, 'G' FROM T_REG_COMPONENTS R INNER JOIN T_COMPONENTS C ON R.F_CAS_NUMBER_COMPONENT_ID = C.F_CAS_NUMBER_COMPONENT_ID
			WHERE R.F_REGULATION = v_GEN_REG_DC
		) T1 ON F.F_CAS_NUMBER=T1.CAS AND (T1.COMPID='LOLI' OR T1.COMPID=F.F_COMPONENT_ID)
		LEFT JOIN (
			/* Priority Substances */
			SELECT CAS, 'LOLI' COMPID, 'P' T FROM LISTDATA WHERE LISTID=4589
			UNION
			SELECT R.F_CAS_NUMBER, R.F_COMPONENT_ID, 'P' FROM T_REG_COMPONENTS R INNER JOIN T_COMPONENTS C ON R.F_CAS_NUMBER_COMPONENT_ID = C.F_CAS_NUMBER_COMPONENT_ID
			WHERE R.F_REGULATION = v_PRI_REG_DC
		) T2 ON F.F_CAS_NUMBER=T2.CAS AND (T2.COMPID='LOLI' OR T2.COMPID=F.F_COMPONENT_ID)
		LEFT JOIN (
			/* Monitoring Substances */
			SELECT CAS, 'LOLI' COMPID, 'M' T FROM LISTDATA WHERE LISTID=634
			UNION
			SELECT R.F_CAS_NUMBER, R.F_COMPONENT_ID, 'M' FROM T_REG_COMPONENTS R INNER JOIN T_COMPONENTS C ON R.F_CAS_NUMBER_COMPONENT_ID = C.F_CAS_NUMBER_COMPONENT_ID
			WHERE R.F_REGULATION = v_MON_REG_DC
		) T3 ON F.F_CAS_NUMBER=T3.CAS AND (T3.COMPID='LOLI' OR T3.COMPID=F.F_COMPONENT_ID)
	) CSCL ON V.F_CAS_NUMBER = CSCL.CAS
    WHERE
		V.F_TRANS_DATE BETWEEN v_REPORT_PERIOD_START AND v_REPORT_PERIOD_END				-- Grab only the transactions within the specified reporting period
		---AND V.F_LEGAL_ENTITY_ID IN ( SELECT DATA FROM dbo.SPLIT(@LEGAL_ENTITY, ',') )	-- Grab only the transactions relevant to the legal entities configured for CSCL reporting
        AND V.F_LEGAL_ENTITY_ID IN (SELECT * FROM table(fnSPLIT(v_legal_entities, ',')) )	-- Grab only the transactions relevant to the legal entities configured for CSCL reporting
        --AND CSCL.T IN ( SELECT DATA FROM dbo.SPLIT(@CSCL_CLASS, ',') )					-- Grab only the transactions for chemicals that are classified under CSCL
		AND CSCL.T IN (SELECT * FROM table(fnSPLIT(v_CSCL_CLASS, ',')) )					-- Grab only the transactions for chemicals that are classified under CSCL
    ;

	/* Step 3 : Aggregate converted volume data, filter out unnecessary volume data (ie volumes that are under threshold), round the numbers  units (kg -> ton) to complete the report */
    OPEN p_rs_results FOR
		SELECT
            -- Legal Entity ID
            F_LEGAL_ENTITY_ID "",

            -- CSCL Classification
            CASE T
                WHEN 'G' THEN u'\00e4\00b8\20ac\00e8\02c6\00ac\00e5\0152\2013\00e5\00ad\00a6\00e7\2030\00a9\00e8\00b3\00aa'		-- General
                WHEN 'P' THEN u'\00e5\201e\00aa\00e5\2026\02c6\00e8\00a9\2022\00e4\00be\00a1\00e5\0152\2013\00e5\00ad\00a6\00e7\2030\00a9\00e8\00b3\00aa'	-- Priority
                WHEN 'M' THEN u'\00e7\203a\00a3\00e8\00a6\2013\00e5\0152\2013\00e5\00ad\00a6\00e7\2030\00a9\00e8\00b3\00aa'		-- Monitoring
            	ELSE u''
            END "",

            -- CAS RN
            Z.F_CAS_NUMBER "CAS RN",

            -- METI Number
            TO_NCHAR(REPLACE(REPLACE(REPLACE(NVL(METINUM.F_DATA, ''), ' ', ''), '|par', u'?'), ',', u'?')) "??",

    		-- Substance Control Number
        	CASE T
            	WHEN 'P' THEN NVL(PRI_SUB_CONTROL_NUM.F_DATA, '')	-- Priority
                WHEN 'M' THEN NVL(MON_SUB_CONTROL_NUM.F_DATA, '')	-- Monitoring
                ELSE u''
            END "??",

            -- Chemical Name (General name). Try fetching the Japanese name then fall back to English name
            NVL(CA.F_CHEM_NAME, C.F_CHEM_NAME) "??",

            -- Chemical Name as listed in regulation (CVAL)
            CASE T
                WHEN 'P' THEN NVL(PRI_LISTED_NAME.F_DATA, '')	-- Priority
                WHEN 'M' THEN NVL(MON_LISTED_NAME.F_DATA, '')	-- Monitoring
                ELSE u''
            END "??",

            -- Transaction Type
            CASE F_TRANS_TYPE_CONVERTED
                WHEN 1 THEN u'\00e8\00bc\00b8\00e5\2026\00a5'
                WHEN 2 THEN u'\00e8\00a3\00bd\00e9\20ac\00a0'
                WHEN 3 THEN u'\00e5\2021\00ba\00e8\008d\00b7'
                ELSE u''
            END "??",

        	-- Shipped FROM Country Name
            NVL(
                (
                    SELECT C.F_NAME FROM T_MAP_LOCATIONS M
                    INNER JOIN T_MAP_LOCATION_LANG_NAMES C ON M.F_RECORD_GUID=C.F_LOCATION_GUID
                    WHERE M.F_LOCATION_TYPE=0 AND M.F_LOCATION_ID=F_SHIPPED_FROM_COUNTRY_CONVERT AND C.F_LANGUAGE='JP'
                ), ''
            ) "",

            -- Shipped FROM Country Code
            NVL(
                (
                    SELECT F_CUSTOM1 FROM T_MAP_LOCATIONS WHERE F_LOCATION_TYPE=0 AND F_LOCATION_ID=F_SHIPPED_FROM_COUNTRY_CONVERT
    			), ''
        	) "",

            -- Shipped TO Code
            NVL(F_SHIPPED_TO_ID_CONVERTED, '') "/",


            -- Shipped TO Country Name
            NVL(
                (
                    SELECT C.F_NAME FROM T_MAP_LOCATIONS M
                	INNER JOIN T_MAP_LOCATION_LANG_NAMES C ON M.F_RECORD_GUID=C.F_LOCATION_GUID
                    WHERE M.F_LOCATION_TYPE=0 AND M.F_LOCATION_ID=F_SHIPPED_TO_COUNTRY_CONVERTED AND C.F_LANGUAGE='JP'
                ), ''
            ) "?",

            -- Shipped TO Country Code
            NVL(
                (
                    SELECT F_CUSTOM1 FROM T_MAP_LOCATIONS WHERE F_LOCATION_TYPE=0 AND F_LOCATION_ID=F_SHIPPED_TO_COUNTRY_CONVERTED
                ), ''
            ) "?",

            -- Shipped TO State (Prefecture) Name
            NVL(
                (
                    SELECT C.F_NAME FROM T_MAP_LOCATIONS M
    				INNER JOIN T_MAP_LOCATION_LANG_NAMES C ON M.F_RECORD_GUID=C.F_LOCATION_GUID
        			WHERE M.F_LOCATION_TYPE=1 AND M.F_LOCATION_ID=F_SHIPPED_TO_STATE_CONVERTED AND C.F_LANGUAGE='JP'
            	), ''
    		) "?",

    		-- Shipped TO State (Prefecture) Code
        	NVL(F_SHIPPED_TO_STATE_CONVERTED, '') "?",

            -- Usage Code: The first 3-digit portion
            NVL(
                (
                    -- The Split function is standard stuff since 71.6
                    --SELECT DATA FROM dbo.SPLIT(F_CSCL_USAGE_CODE_CONVERTED, '-') WHERE ID=1
                    SELECT * FROM TABLE(FNSPLIT(F_CSCL_USAGE_CODE_CONVERTED, '-')) WHERE ROWNUM=1
                ), ''
            ) "",

            -- Usage Code: The latter alphabet portion
            NVL(
            	(
            		-- The Split function is standard stuff since 71.6
            		--SELECT DATA FROM dbo.SPLIT(F_CSCL_USAGE_CODE_CONVERTED, '-') WHERE ID=2
                    SELECT * FROM TABLE(FNSPLIT(F_CSCL_USAGE_CODE_CONVERTED, '-')) WHERE ROWNUM=2
            	), ''
            ) "()",

    		-- Usage Description phrase
        	CASE
            	WHEN F_CSCL_USAGE_CODE_CONVERTED LIKE '%[0-9]-[A-Z]' THEN
                	-- If usage includes detailed codes (ie alphabets) then grab the description from T_CSCL_USE_CODES
                    NVL(
                        (SELECT REPLACE(F_PHRASE_JP, F_USE_CODE || ';', '') FROM T_CSCL_USE_CODES WHERE F_USE_CODE=F_CSCL_USAGE_CODE_CONVERTED ),
                        ''
                    )
                ELSE
                    COALESCE(( SELECT REPLACE(F_PHRASE_JP, TO_NCHAR(F_CATEGORY_CODE) || '; ', '') FROM T_CSCL_USE_CATEGORIES WHERE TO_NCHAR(F_CATEGORY_CODE)=F_CSCL_USAGE_CODE_CONVERTED ), u'')

            END "",

            -- Polymer Indicator (use the F_POLYMER_INDICATOR associated with component function CSCLF004 (FTXT))
            CASE
                WHEN F_POLYMER_INDICATOR=1 THEN u'\00e3\20ac\2021'				-- In Japan we typically use ? instead of X to indicate "applicable"
                ELSE u''
            END "?",

            -- Salt Indicator (CVAL)
            CASE
            	WHEN SALT_INDICATOR.F_DATA IS NOT NULL THEN u'\00e3\20ac\2021'	-- In Japan we typically use ? instead of X to indicate "applicable"
            	ELSE u''
            END "",

            -- Complex Oxide (COX) Indicator (CVAL)
            CASE
                WHEN COX_INDICATOR.F_DATA IS NOT NULL THEN u'\00e3\20ac\2021'	-- In Japan we typically use ? instead of X to indicate "applicable"
    			ELSE u''
        	END "?",

        	-- Rounded Annual Volume
        	CASE
             	WHEN T IN ('G', 'P') THEN CAST(ROUND(ANNUAL_VOLUME / 1000, 0) AS INT)	-- Use metric tons for General 
            	ELSE CAST(ROUND(ANNUAL_VOLUME, 0) AS INT)	-- Use kilograms for Monitoring
            END "?",

            -- UOM
            CASE
            	WHEN T IN ('G', 'P') THEN 't'	-- Use metric tons for General 
            	ELSE 'kg'	-- Use kilograms for Monitoring
            END "??"

        FROM (
            SELECT
                F_LEGAL_ENTITY_ID, F_CAS_NUMBER, F_COMPONENT_ID, T, F_POLYMER_INDICATOR, F_TRANS_TYPE_CONVERTED, F_SHIPPED_FROM_COUNTRY_CONVERT,
                F_SHIPPED_TO_ID_CONVERTED, F_SHIPPED_TO_COUNTRY_CONVERTED, F_SHIPPED_TO_STATE_CONVERTED, F_CSCL_USAGE_CODE_CONVERTED,
                SUM(F_QUANTITY * AGGREGATE_METHOD) ANNUAL_VOLUME
            FROM TEMP_SVT_CSCL
            WHERE
                F_TRANS_TYPE_CONVERTED IN (1, 2, 3)
                AND T IN ('P', 'M', 'G')				-- Make sure the chemicals fall into one of the three CSCL classifications
            GROUP BY
                F_LEGAL_ENTITY_ID, F_CAS_NUMBER, F_COMPONENT_ID, T, F_POLYMER_INDICATOR, F_TRANS_TYPE_CONVERTED, F_SHIPPED_FROM_COUNTRY_CONVERT,
                F_SHIPPED_TO_ID_CONVERTED, F_SHIPPED_TO_COUNTRY_CONVERTED, F_SHIPPED_TO_STATE_CONVERTED, F_CSCL_USAGE_CODE_CONVERTED
        ) Z

        -- Grab the English chemical name for fallback
        INNER JOIN T_COMPONENTS C ON Z.F_CAS_NUMBER = C.F_CAS_NUMBER AND Z.F_COMPONENT_ID = C.F_COMPONENT_ID

        -- METI NUMBER
        INNER JOIN T_COMP_DATA METINUM ON Z.F_CAS_NUMBER + Z.F_COMPONENT_ID=METINUM.F_CAS_NUMBER_COMPONENT_ID AND METINUM.F_DATA_CODE = v_METINUM_DC

    	-- Grab the Japanese chemical name if available
        LEFT JOIN T_COMPONENTS_ALIAS CA ON C.F_CAS_NUMBER_COMPONENT_ID=CA.F_CAS_NUMBER_COMPONENT_ID AND CA.F_LANGUAGE='JP'

        -- Grab the Substance Control Number for Priority Chemicals if available
        LEFT JOIN T_COMP_DATA PRI_SUB_CONTROL_NUM ON Z.F_CAS_NUMBER = PRI_SUB_CONTROL_NUM.F_CAS_NUMBER AND Z.F_COMPONENT_ID = PRI_SUB_CONTROL_NUM.F_COMPONENT_ID AND PRI_SUB_CONTROL_NUM.F_DATA_CODE=v_PCN_DC

        -- Grab the Listed Name for Priority Chemicals if available
        LEFT JOIN T_COMP_DATA PRI_LISTED_NAME ON Z.F_CAS_NUMBER = PRI_LISTED_NAME.F_CAS_NUMBER AND Z.F_COMPONENT_ID = PRI_LISTED_NAME.F_COMPONENT_ID AND PRI_LISTED_NAME.F_DATA_CODE=v_PLN_DC

        -- Grab the Substance Control Number for Monitoring Chemicals if available
        LEFT JOIN T_COMP_DATA MON_SUB_CONTROL_NUM ON Z.F_CAS_NUMBER = MON_SUB_CONTROL_NUM.F_CAS_NUMBER AND Z.F_COMPONENT_ID = MON_SUB_CONTROL_NUM.F_COMPONENT_ID AND MON_SUB_CONTROL_NUM.F_DATA_CODE=v_MCN_DC

        -- Grab the Listed Name for Monitoring Chemicals if available
        LEFT JOIN T_COMP_DATA MON_LISTED_NAME ON Z.F_CAS_NUMBER = MON_LISTED_NAME.F_CAS_NUMBER AND Z.F_COMPONENT_ID = MON_LISTED_NAME.F_COMPONENT_ID AND MON_LISTED_NAME.F_DATA_CODE=v_MLN_DC

        -- Grab the Salt Indicator if available
        LEFT JOIN T_COMP_DATA SALT_INDICATOR ON Z.F_CAS_NUMBER = SALT_INDICATOR.F_CAS_NUMBER AND Z.F_COMPONENT_ID = SALT_INDICATOR.F_COMPONENT_ID AND SALT_INDICATOR.F_DATA_CODE=v_SALT_DC

        -- Grab the COX Indicator if available
        LEFT JOIN T_COMP_DATA COX_INDICATOR ON Z.F_CAS_NUMBER = COX_INDICATOR.F_CAS_NUMBER AND Z.F_COMPONENT_ID = COX_INDICATOR.F_COMPONENT_ID AND COX_INDICATOR.F_DATA_CODE=v_COX_DC
        WHERE
            -- Filter out the records that shouldn't go on the report
            -- Don't report volumes under threshold
            (T IN ('G', 'P') AND ANNUAL_VOLUME >= 1000)	-- DO NOT report annual volumes less than 1 metric ton (1,000 kg) for General/Priority substances
            OR
            (T = 'M' AND ANNUAL_VOLUME >= 1)	-- DO NOT report annual volumes less than 1 kg for Monitoring substances
        ORDER BY
            F_LEGAL_ENTITY_ID, CASE T WHEN 'G' THEN 1 WHEN 'P' THEN 2 WHEN 'M' THEN 3 ELSE 4 END, Z.F_CAS_NUMBER, F_TRANS_TYPE_CONVERTED,
            F_SHIPPED_FROM_COUNTRY_CONVERT, F_SHIPPED_TO_ID_CONVERTED, F_SHIPPED_TO_COUNTRY_CONVERTED, F_SHIPPED_TO_STATE_CONVERTED, F_CSCL_USAGE_CODE_CONVERTED;

end;

