  CREATE OR REPLACE EDITIONABLE FUNCTION "WERCS"."NEWID" RETURN VARCHAR2 IS
    guid VARCHAR2(36);
BEGIN
    guid := SYS_GUID();
    guid :=
               SUBSTR(guid,  1, 8) ||
        '-' || SUBSTR(guid,  9, 4) ||
        '-' || SUBSTR(guid, 13, 4) ||
        '-' || SUBSTR(guid, 17, 4) ||
        '-' || SUBSTR(guid, 21);
    RETURN guid;
END NEWID;
 
 
 

